const { chromium } = require("playwright");
const { textToSpeechBase64 } = require("../../services/ttsService");
const FocusGroupSession = require("../../models/FocusGroupSession");
const path = require("path");

async function joinMeetAndAskQuestions(meetingLink, questions, sessionId) {
  const userDataDir = "C:/meetingbot-profile";

  console.log("Launching Chrome with bot profile...");

  const context = await chromium.launchPersistentContext(userDataDir, {
    channel: "chrome",
    headless: false,
    args: [
      "--start-maximized",
      "--disable-blink-features=AutomationControlled",
    ],
    permissions: ["microphone", "camera"],
  });

  const page = await context.newPage();
  await page.waitForTimeout(5000);
  console.log("Using meeting link:", meetingLink);
  console.log("Opening meeting link...");
  await page.goto(meetingLink, { waitUntil: "domcontentloaded" });

  console.log("Waiting for Join button...");
  const joinButton = page.locator(
    'button:has-text("Join now"), button:has-text("Ask to join")',
  );

  await joinButton.first().waitFor({ timeout: 40000 });
  console.log("Clicking Join...");
  await joinButton.first().click();
  await page
    .locator('button[aria-label*="captions"]')
    .click()
    .catch(() => {});

  startAutoAdmit(page);

  console.log("Bot joined. Stabilizing...");
  await page.waitForTimeout(45000);
  console.log("Bot is successfully inside the meeting!");
  console.log("DO NOT CLOSE THIS WINDOW");

  console.log("Greeting participants...");
  await playGreeting(page);
  await page.waitForTimeout(10000);
  for (let i = 0; i < questions.length; i++) {
    const questionText = questions[i];

    await speakText(page, `Question ${i + 1}. ${questionText}`);
  }
  const session = await FocusGroupSession.findById(sessionId);

  session.status = "completed";
  session.botStatus = "ended";
  session.endedAt = new Date();
  await session.save();
  console.log("Focus group session completed and saved");
  if (page._autoAdmitInterval) {
    clearInterval(page._autoAdmitInterval);
  }

  await new Promise(() => {});
}

async function playGreeting(page) {
  console.log("Playing greeting audio...");

  const base64Audio =
    "";

  await page.evaluate(async (base64) => {
    const audio = document.createElement("audio");
    audio.src = `data:audio/mp3;base64,${base64}`;
    audio.autoplay = true;
    audio.volume = 1.0;
    document.body.appendChild(audio);

    await audio.play();
  }, base64Audio);
}

async function startAutoAdmit(page) {
  console.log("Auto-admit enabled");

  const interval = setInterval(async () => {
    try {
      const admitButtons = await page.$$('button:has-text("Admit")');

      for (const button of admitButtons) {
        try {
          const isVisible = await button.isVisible();
          if (!isVisible) continue;

          console.log("Admitting a participant...");
          await button.click();
          await page.waitForTimeout(500);
        } catch (_) {}
      }
    } catch (_) {}
  }, 2000);
  page._autoAdmitInterval = interval;
}

async function speakText(page, text) {
  const base64Audio = await textToSpeechBase64(text);

  await page.evaluate(async (base64) => {
    const audio = document.createElement("audio");
    audio.src = `data:audio/mp3;base64,${base64}`;
    audio.autoplay = true;
    audio.volume = 1.0;
    document.body.appendChild(audio);
    await audio.play();
  }, base64Audio);
}

module.exports = { joinMeetAndAskQuestions };
